mapper.select:
select
`id`,`name`,`value`,`type`,`description`,`sort`,`parent_id`,`create_by`,`create_date`,`update_by`,`update_date`,`remarks`,`del_flag`
from sys_dict

	 and id = #{id} 
	 and name = #{name} 
	 and value = #{value} 
	 and type = #{type} 
	 and description = #{description} 
	 and sort = #{sort} 
	 and parent_id = #{parentId} 
	 and create_by = #{createBy} 
	 and create_date = #{createDate} 
	 and update_by = #{updateBy} 
	 and update_date = #{updateDate} 
	 and remarks = #{remarks} 
	 and del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by sort


		order by id



	limit #{offset}, #{limit}

select count(*) from sys_dict

	 and id = #{id} 
	 and name = #{name} 
	 and value = #{value} 
	 and type = #{type} 
	 and description = #{description} 
	 and sort = #{sort} 
	 and parent_id = #{parentId} 
	 and create_by = #{createBy} 
	 and create_date = #{createDate} 
	 and update_by = #{updateBy} 
	 and update_date = #{updateDate} 
	 and remarks = #{remarks} 
	 and del_flag = #{delFlag}

SELECT t.province AS province,
c.city AS city,
ct.area AS area,
null AS '' FROM tb_province t
LEFT JOIN tb_city c ON t.provinceid = c.provinceid
LEFT JOIN tb_county ct ON c.cityid = ct.cityid
WHERE t.provinceid = #{provinceid}
and c.cityid = #{cityid}
and ct.areaid = #{countyid}
